mapper.update:
UPDATE `team`
SET team_name = #{teamName}, team_info = #{teamInfo}
WHERE team_id = #{teamId}

mapper.select:
SELECT user_perm
FROM `team_member`
WHERE user_id = #{userId}
  AND team_id = #{teamId}

mapper.update:
UPDATE `team_member`
SET user_perm = #{userPerm}
WHERE user_id = #{userId}
  AND team_id = #{teamId}

mapper.delete:
DELETE
FROM `team`
WHERE team_id = #{teamId};
DELETE
FROM `team_member`
WHERE team_id = #{teamId};

mapper.select:
SELECT DISTINCT *
FROM `team` NATURAL JOIN `team_member`
WHERE user_id = #{userId}

SELECT DISTINCT user_id, username, email, real_name, user_perm
FROM `user` NATURAL JOIN `team_member`
WHERE team_id = #{teamId}

mapper.delete:
DELETE
FROM `team_member`
WHERE user_id = #{userId}
  AND team_id = #{teamId}
