mapper.update:
UPDATE `team`
SET team_name = #{teamName},
    team_info = #{teamInfo}
WHERE team_id = #{teamId}

mapper.select:
SELECT user_perm
FROM `team_member`
WHERE user_id = #{userId}
  AND team_id = #{teamId}

mapper.update:
UPDATE `team_member`
SET user_perm = #{userPerm}
WHERE user_id = #{userId}
  AND team_id = #{teamId}

mapper.delete:
DELETE
FROM `team_member`
WHERE user_id = #{userId}
  AND team_id = #{teamId}

mapper.select:
SELECT lastViewedTime
FROM `user_team`
WHERE user_id = #{user_id}
  AND team_id = #{teamId}

mapper.update:
UPDATE `user_team`
SET lastViewedTime = #{lastViewedTime}
WHERE user_id = #{user_id}
  AND team_id = #{teamId}
