mapper.select:
SELECT user_perm
FROM `team_member`
WHERE user_id = #{userId}
  AND team_id = #{teamId}

mapper.delete:
DELETE
FROM `team`
WHERE team_id = #{team_id};
DELETE
FROM `team_member`
WHERE team_id = #{team_id};

mapper.select:
SELECT DISTINCT *
FROM `team` NATURAL JOIN `team_member`
WHERE user_id = #{userId}

SELECT DISTINCT user_id, username, email, real_name
FROM `user` NATURAL JOIN `team_member`
WHERE user_id = #{userId}
